	void start() {
		int choice = -1;
		
		while(true) {
			Util::cls();
			choice = printMenu();
			
			if (choice == 1) {
				Game::startGame();
			} else if (choice == 2) {
				break;
			}
		}
		
		Util::cls();
		printExitLogo();
		Util::enterToContinue();
	}

